mapper.select:
/* TycCustomer.getCustomerIdCount : 고객 아이디 존재 여부 체크 */
SELECT COUNT(1) AS COUNT
FROM   TB_CUST CT
WHERE  CUST_ID = #{custId}

mapper.update:
/* TycCustomer.updateTbCustRegNo : 등록 고객 번호 custNo로 지정 */
UPDATE TB_CUST SET
       REG_NO = #{regNo}
     , UDT_NO = #{udtNo}
     , UDT_DT = NOW()
WHERE  CUST_NO = #{custNo}
